import {
  View,
  Text,
  TouchableOpacity,
  Image,
  TextInput,
  ActivityIndicator,
} from "react-native";
import React, { useEffect, useState } from "react";
import { useNavigation } from "@react-navigation/native";
import { SelectList } from "react-native-dropdown-select-list";
import { ScrollView } from "react-native-gesture-handler";
import Toast from "react-native-toast-message";
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";

export default function Form({ id, setProfile, city }) {
  const API_URL = "/request";
  const navigation = useNavigation();
  const [title, setTitle] = useState("");
  const [desc, setDes] = useState("");
  const [price, setPrice] = useState("");
  const [loading, setLoading] = useState(false);

  const data = [
    { key: "1", value: "Flat" },
    { key: "2", value: "Biding" },
  ];
  const [categories, setCategories] = useState(null);
  const [selected, setSelected] = useState("");
  const [selectCat, setSelectCat] = useState();
  useEffect(() => {
    axios
      .get(`/category`)
      .then((response) => {
        const mappedCategories = response.data.map((category) => {
          return {
            value: category.category,
            label: category.category,
          };
        });
        setCategories(mappedCategories);
        console.log(mappedCategories);
      })
      .catch((error) => {
        console.log(error);
      });
    console.log(city);
  }, []);
  const handleSubmit = () => {
    if (
      title.trim() === "" ||
      desc.trim() === "" ||
      selected.trim() === "" ||
      price.trim() === "" ||
      selectCat === null
    ) {
      Toast.show({
        type: "error",
        position: "top",
        text1: "please fill details",
        visibilityTime: 3000,
      });
    } else {
      setLoading(true);
      sendData();
    }
  };
  const sendData = async () => {
    try {
      const res = await axios.post(API_URL, {
        title,
        description: desc,
        rateType: selected,
        price,
        buyerId: id,
        user: [],
        city,
        category: selectCat,
      });
      if (res.data) {
        setLoading(false);
        Toast.show({
          type: "success",
          position: "top",
          text1: "successfully added",
          visibilityTime: 3000,
        });
        setProfile(false);
      }
    } catch (error) {
      console.log(error.message + " error occured");
    }
  };

  return (
    <ScrollView showsVerticalScrollIndicator={false}>
      <View className="form space-y-2">
        <Text className="text-gray-700 ml-4">Title</Text>
        <TextInput
          className="p-4 bg-gray-100 text-gray-700 rounded-2xl mb-3"
          onChangeText={(value) => setTitle(value)}
        />
        <Text className="text-gray-700 ml-4">Description</Text>
        <TextInput
          className="p-4 bg-gray-100 text-gray-700 rounded-2xl mb-3"
          onChangeText={(value) => setDes(value)}
        />
        <SelectList
          setSelected={(val) => setSelected(val)}
          data={data}
          placeholder="Choose payment type"
          save="value"
          search={false}
          className="p-4 bg-gray-100 text-gray-700 rounded-2xl  mb-3"
          boxStyles={{
            borderWidth: 0,
            padding: 4,
            backgroundColor: "rgb(243,244,246)",
            marginBottom: 6,
          }}
          dropdownStyles={{
            borderWidth: 0,
            backgroundColor: "rgb(243,244,246)",
          }}
        />
        {categories && (
          <SelectList
            setSelected={(val) => setSelectCat(val)}
            data={categories}
            placeholder="Choose Category"
            save="value"
            search={false}
            className="p-4 bg-gray-100 text-gray-700 rounded-2xl"
            boxStyles={{
              borderWidth: 0,
              padding: 4,
              backgroundColor: "rgb(243,244,246)",
              marginBottom: 4,
            }}
            dropdownStyles={{
              borderWidth: 0,
              backgroundColor: "rgb(243,244,246)",
            }}
          />
        )}
        <Text className="text-gray-700 ml-4">Price</Text>
        <TextInput
          className="p-4 bg-gray-100 text-gray-700 rounded-2xl mb-3"
          onChangeText={(value) => setPrice(value)}
        />
        <TouchableOpacity
          className="py-3   bg-yellow-400 rounded-xl"
          onPress={handleSubmit}
        >
          <Text className="text-xl font-bold text-center text-gray-700">
            Submit
          </Text>
        </TouchableOpacity>
      </View>
      {loading && (
        <View className="flex justify-center items-center w-full h-full z-40 absolute">
          <ActivityIndicator size="large" color="#0000ff" />
        </View>
      )}
    </ScrollView>
  );
}
