import {
  View,
  Text,
  TouchableOpacity,
  Image,
  TextInput,
  ActivityIndicator,
} from "react-native";
import React, { useEffect, useState } from "react";
import { SafeAreaView } from "react-native-safe-area-context";
import { AntDesign } from "@expo/vector-icons";
import { themeColors } from "../theme";
import { useNavigation } from "@react-navigation/native";
import Toast from "react-native-toast-message";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";
import { useIsFocused } from "@react-navigation/native";
export default function Login() {
  const navigation = useNavigation();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const isFocused = useIsFocused();
  useEffect(() => {
    if (isFocused) {
      getValueFromAsyncStorage();
    }
  }, [isFocused]);
  async function getValueFromAsyncStorage() {
    try {
      const value = await AsyncStorage.getItem("accountType");
      if (value !== null) {
        console.log(value);
        if (value == "Client") {
          navigation.navigate("Dashboard");
        }
        if (value == "Mazdoor") {
          navigation.navigate("SellerTabs");
        }
        if (value == "Admin") {
          navigation.navigate("AdminTabs");
        }
      } else {
        console.log("Value not found.");
      }
    } catch (error) {
      console.log(error);
    }
  }

  const handleSubmit = () => {
    if (email.trim() === "" || password.trim() === "") {
      Toast.show({
        type: "error",
        position: "top",
        text1: "please fill all details",
        visibilityTime: 3000,
      });
    } else {
      loginCheck();
      setLoading(true);
    }
  };
  const loginCheck = async () => {
    try {
      const res = await axios.post("/login", {
        email,
        password,
      });
      if (res.data) {
        setLoading(false);
        Toast.show({
          type: "success",
          position: "top",
          text1: "successfully login!",
          visibilityTime: 3000,
        });
        await AsyncStorage.setItem("name", res.data.name);
        await AsyncStorage.setItem("accountType", res.data.accountType);
        await AsyncStorage.setItem("id", res.data._id);
        if (res.data.location?.city) {
          await AsyncStorage.setItem("city", res.data?.location?.city);
          console.log(res.data?.location?.city);
        }
        if (res.data.accountType == "Client") {
          navigation.navigate("Dashboard");
        }
        if (res.data.accountType == "Mazdoor") {
          navigation.navigate("SellerTabs");
        }
        if (res.data.accountType == "Admin") {
          navigation.navigate("AdminTabs");
        }
      }
    } catch (error) {
      console.log(error.message + " error occured");
      setLoading(false);
      Toast.show({
        type: "error",
        position: "top",
        text1: "Something went wrong!",
        visibilityTime: 3000,
      });
    }
  };
  return (
    <View
      className="flex-1 bg-white"
      style={{ backgroundColor: themeColors.bg }}
    >
      <SafeAreaView className="flex ">
        <View className="flex-row justify-start"></View>
        <View className="flex-row justify-center">
          <Image
            source={require("../assets/images/login.png")}
            style={{ width: 200, height: 200 }}
          />
        </View>
      </SafeAreaView>
      <View
        style={{ borderTopLeftRadius: 50, borderTopRightRadius: 50 }}
        className="flex-1 bg-white px-8 pt-8"
      >
        <View className="form space-y-2">
          <Text className="text-gray-700 ml-4">Email Address</Text>
          <TextInput
            onChangeText={(value) => setEmail(value)}
            className="p-4 bg-gray-100 text-gray-700 rounded-2xl mb-3"
            placeholder="email"
            keyboardType="email-address"
          />
          <Text className="text-gray-700 ml-4">Password</Text>
          <TextInput
            onChangeText={(value) => setPassword(value)}
            className="p-4 bg-gray-100 text-gray-700 rounded-2xl"
            secureTextEntry
            placeholder="password"
          />

          <TouchableOpacity
            className="py-3  bg-yellow-400 rounded-xl"
            onPress={handleSubmit}
          >
            <Text className="text-xl font-bold text-center text-gray-700">
              Login
            </Text>
          </TouchableOpacity>
        </View>

        <View className="flex-row justify-center mt-7">
          <Text className="text-gray-500 font-semibold">
            Don't have an account?
          </Text>
          <TouchableOpacity onPress={() => navigation.navigate("SignUp")}>
            <Text className="font-semibold text-yellow-500"> Sign Up</Text>
          </TouchableOpacity>
        </View>
      </View>
      {loading && (
        <View className="flex justify-center items-center w-full h-full z-40 absolute">
          <ActivityIndicator size="large" color="#0000ff" />
        </View>
      )}
    </View>
  );
}
