import {
  View,
  Text,
  Image,
  TouchableOpacity,
  Modal,
  Pressable,
  Alert,
  ScrollView,
  ActivityIndicator,
} from "react-native";
import React, { useEffect, useState } from "react";
import { LinearGradient } from "expo-linear-gradient";
import { storeColors } from "../theme";
import StarRating from "react-native-star-rating";
import { AntDesign } from "@expo/vector-icons";
import { useNavigation } from "@react-navigation/native";
import * as Animatable from "react-native-animatable";
import MapView, { Marker } from "react-native-maps";
import { SafeAreaView } from "react-native";
import GradientButton from "../components/gradientButton";
import { useRoute } from "@react-navigation/native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";
import { useIsFocused } from "@react-navigation/native";
import {
  requestForegroundPermissionsAsync,
  getCurrentPositionAsync,
} from "expo-location";
import MapViewDirections from "react-native-maps-directions";

export default function FoodDetailsScreen() {
  const [modalVisible, setModalVisible] = useState(false);
  const [payment, setPayment] = useState(null);
  const route = useRoute();
  const { labor, reqId, price, id, total } = route.params;
  const [data, setData] = useState();
  const isFocused = useIsFocused();
  axios.defaults.withCredentials = true;
  const navigation = useNavigation();
  const [currentLocation, setCurrentLocation] = useState(null);
  const [directions, setDirection] = useState(null);

  useEffect(() => {
    checkPermission();
    getData(labor._id);
  }, []);
  const checkPermission = async () => {
    const { granted } = await requestForegroundPermissionsAsync();
    if (granted) {
      const { coords } = await getCurrentPositionAsync({});
      if (coords) {
        setCurrentLocation(coords);
        console.log("set value");
      }
    }
  };
  function getData(id) {
    axios
      .get(`/users`, {
        params: {
          id,
        },
      })
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }
  const handleOrder = () => {
    console.log("click");
    axios
      .post(`/order`, {
        labor: labor._id,
        buyerId: id,
        price,
        reqId,
        status: "pending",
      })
      .then((response) => {
        navigation.goBack();
      })
      .catch((error) => {
        console.log(error);
      });
  };

  if (!currentLocation) {
    return (
      <View className="flex justify-center items-center w-full h-full z-40 absolute">
        <ActivityIndicator size="large" color="#0000ff" />
      </View>
    );
  }

  if (!data) {
    return (
      <View className="flex justify-center items-center w-full h-full z-40 absolute">
        <ActivityIndicator size="large" color="#0000ff" />
      </View>
    );
  }

  return (
    <LinearGradient
      colors={["rgba(58, 131, 244,0.4)", "rgba(9, 181, 211, 0.4)"]}
      className="w-full flex-1"
    >
      <ScrollView>
        <View className="relative">
          <Image
            source={{ uri: `data:image/png;base64,${data.image}` }}
            className="w-full h-60 rounded-3xl"
          />
          <LinearGradient
            colors={["transparent", "rgba(0, 0, 0, 0.6)"]}
            className="absolute p-4 h-full w-full flex justify-between rounded-3xl"
          >
            <View className="flex-row justify-start mt-5">
              <TouchableOpacity
                onPress={() => navigation.goBack()}
                className="p-3 rounded-full"
                style={{ backgroundColor: "rgba(255,255,255,0.3)" }}
              >
                <AntDesign name="back" size={24} color="black" />
              </TouchableOpacity>
            </View>
            <View className="space-y-1">
              <Text className="text-xl font-bold text-gray-300">
                {data.name}
              </Text>
              <StarRating
                disabled={true}
                starSize={15}
                containerStyle={{ width: 90 }}
                maxStars={5}
                halfStarColor="gold"
                rating={Number(total)}
                emptyStar={require("../assets/images/emptyStar.png")}
                fullStar={require("../assets/images/fullStar.png")}
              />
            </View>
          </LinearGradient>
        </View>
        <View className="mx-4 mt-6 space-y-3 h-60 rounded-xl overflow-hidden">
          {data.location && (
            <MapView
              className="flex-1"
              showsUserLocation={true}
              followsUserLocation={true}
              initialRegion={{
                latitude:
                  (currentLocation.latitude + data.location.latitude) / 2,
                longitude:
                  (currentLocation.longitude + data.location.longitude) / 2,
                latitudeDelta:
                  Math.abs(currentLocation.latitude - data.location.latitude) *
                  1.5,
                longitudeDelta:
                  Math.abs(
                    currentLocation.longitude - data.location.longitude
                  ) * 1.5,
              }} //your region data goes here.
            >
              {/*Make sure the Marker component is a child of MapView. Otherwise it won't render*/}
              <Marker coordinate={data.location} />

              <MapViewDirections
                origin={currentLocation}
                destination={data.location}
                apikey="AIzaSyCyFGLHNnt1if8fPCyeNh7nK3prXDmlBCA"
                strokeWidth={1}
                strokeColor="black"
              />
            </MapView>
          )}
        </View>
        <View className="mx-4 mt-6  ">
          <Text className="text-3xl font-semibold text-gray-800 mb-2">
            Portfolio
          </Text>
          <View className="rounded-xl p-2 mb-1">
            {data.Portfolio.map((item, key) => {
              return (
                <View
                  className="rounded-xl p-2 mb-1"
                  key={key}
                  style={{ backgroundColor: "rgba(255, 255, 255, 0.4)" }}
                >
                  <Text className="text-gray-600 mb-1 ">
                    Designation : {item.desi}
                  </Text>
                  <Text className="text-gray-600  ">
                    Experience : {item.exp}
                  </Text>
                </View>
              );
            })}
          </View>
        </View>

        <View className="mt-3">
          <Text className="text-3xl font-semibold text-gray-800 mx-4 mt-2">
            Reviews
          </Text>
          <ScrollView
            showsVerticalScrollIndicator={false}
            contentContainerStyle={{ paddingBottom: 20 }}
          >
            {data.Feedback.map((item, index) => {
              let bg = "rgba(255,255,255,0.4)";

              if (data._id === item.receiver._id) {
                return (
                  <TouchableOpacity
                    style={{ backgroundColor: bg }}
                    className="mx-4 p-2 mb-2 flex-row rounded-3xl"
                    onPress={() => navigation.navigate("Single")}
                    key={index}
                  >
                    <Image
                      source={{
                        uri: `data:image/png;base64,${item.sender.image}`,
                      }}
                      style={{ width: 80, height: 80 }}
                      className="rounded-2xl"
                    />
                    <View className="flex-1 flex pl-3 ">
                      <Text
                        style={{ color: storeColors.text }}
                        className="font-semibold"
                      >
                        {item.sender.name}
                      </Text>
                      <Text style={{ color: storeColors.text }}>
                        {item.feedback}
                      </Text>
                      <View className="flex-row space-x-3">
                        <View className="flex-row space-x-1">
                          <StarRating
                            disabled={true}
                            starSize={15}
                            containerStyle={{ width: 90 }}
                            maxStars={5}
                            halfStarColor="gold"
                            rating={Number(item.stars)}
                            emptyStar={require("../assets/images/emptyStar.png")}
                            fullStar={require("../assets/images/fullStar.png")}
                          />
                        </View>
                      </View>
                    </View>
                  </TouchableOpacity>
                );
              }
            })}
          </ScrollView>
        </View>
        <View className="mt-6 flex-row justify-center items-center">
          <View>
            <LinearGradient
              colors={["rgba(9, 181, 211, 0.9)", "rgba(58, 131, 244,0.9)"]}
              end={{ x: 1, y: 1 }}
              start={{ x: 0.1, y: 0.2 }}
              className={`rounded-full`}
            >
              <TouchableOpacity
                className={`p-3 px-4 `}
                onPress={() => {
                  setModalVisible(!modalVisible);
                }}
              >
                <Text className="text-white font-bold">Hire Now</Text>
              </TouchableOpacity>
            </LinearGradient>
          </View>
        </View>
        <Modal
          animationType="slide"
          transparent={true}
          visible={modalVisible}
          onRequestClose={() => {
            Alert.alert("Modal has been closed.");
            setModalVisible(!modalVisible);
          }}
        >
          <View className="w-full h-5/6 bottom-0 absolute bg-white rounded-2xl p-5">
            <View>
              <View className="flex-row justify-end">
                <TouchableOpacity
                  onPress={() => setModalVisible(!modalVisible)}
                  className="p-3 rounded-full"
                  style={{ backgroundColor: "rgba(255,255,255,0.3)" }}
                >
                  <AntDesign name="close" size={24} color="black" />
                </TouchableOpacity>
              </View>
              <View className="mt-6">
                <Text className="text-3xl">Payment Method</Text>
                <TouchableOpacity
                  className="flex-row gap-2 items-center"
                  onPress={() => setPayment("easypaisa")}
                >
                  {payment == "easypaisa" ? (
                    <View className="w-4 h-4 rounded-full border-2 border-black bg-black"></View>
                  ) : (
                    <View className="w-4 h-4 rounded-full border-2 border-black"></View>
                  )}

                  <Text className="text-xl">COD</Text>
                </TouchableOpacity>
                {/* <TouchableOpacity
                  className="flex-row gap-2 items-center"
                  onPress={() => setPayment("jazzcash")}
                >
                  {payment == "jazzcash" ? (
                    <View className="w-4 h-4 rounded-full border-2 border-black bg-black"></View>
                  ) : (
                    <View className="w-4 h-4 rounded-full border-2 border-black"></View>
                  )}
                  <Text className="text-xl">Jazzcash</Text>
                </TouchableOpacity> */}
              </View>
              <TouchableOpacity onPress={handleOrder}>
                <GradientButton
                  value="Pay Now"
                  className="text-xl"
                  buttonClass="py-2 px-5"
                />
              </TouchableOpacity>
            </View>
          </View>
        </Modal>
      </ScrollView>
    </LinearGradient>
  );
}
