import {
  View,
  Text,
  TouchableOpacity,
  Image,
  TextInput,
  Button,
  ActivityIndicator,
  Modal,
  Platform,
  ScrollView,
  SafeAreaView,
} from "react-native";
import React, { useEffect, useRef, useState } from "react";
import { themeColors } from "../theme";
import { AntDesign, Entypo } from "@expo/vector-icons";
import { useNavigation } from "@react-navigation/native";
import * as ImagePicker from "expo-image-picker";
import { SelectList } from "react-native-dropdown-select-list";
import Toast from "react-native-toast-message";
import axios from "axios";
import MapView, { Marker } from "react-native-maps";
import * as Location from "expo-location";

export default function SignUpScreen() {
  const navigation = useNavigation();
  const [imageUri, setImageUri] = useState(null);
  const [type, setType] = useState(null);
  const [selected, setSelected] = useState(null);
  const [name, setName] = useState(null);
  const [email, setEmail] = useState(null);
  const [password, setPassword] = useState(null);
  const [loading, setLoading] = useState(false);
  const [location, setLocation] = useState(null);
  axios.defaults.withCredentials = true;
  const API_URL = "/users";
  const [mapModal, setMapModal] = useState(false);
  const markerRef = useRef(null);
  const [currentLocation, setCurrentLocation] = useState(null);
  const [markerLocation, setMarkerLocation] = useState(null);
  useEffect(() => {
    requestLocationPermission();
  }, []);
  const requestLocationPermission = async () => {
    try {
      if (Platform.OS === "android") {
        const { status } = await Location.requestForegroundPermissionsAsync();
        if (status !== "granted") {
          console.log("Location permission not granted!");
          return;
        }
      } else {
        const granted = await Geolocation.requestAuthorization("whenInUse");
        if (granted !== "granted") {
          console.log("Location permission not granted!");
          return;
        }
      }

      getUserLocation();
    } catch (error) {
      console.log("Error requesting location permission:", error);
    }
  };
  const getUserLocation = async () => {
    try {
      if (Platform.OS === "android") {
        const location = await Location.getCurrentPositionAsync({});
        setCurrentLocation({
          latitude: location.coords.latitude,
          longitude: location.coords.longitude,
        });
        const city = await Location.reverseGeocodeAsync({
          latitude: location.coords.latitude,
          longitude: location.coords.longitude,
        });
        setMarkerLocation({
          latitude: location.coords.latitude,
          longitude: location.coords.longitude,
          city: city[0].city,
        });
      } else {
        Geolocation.getCurrentPosition(
          (position) => {
            setCurrentLocation({
              latitude: position.coords.latitude,
              longitude: position.coords.longitude,
            });
            setMarkerLocation({
              latitude: position.coords.latitude,
              longitude: position.coords.longitude,
            });
          },
          (error) => {
            console.log("Error getting location:", error);
          },
          { enableHighAccuracy: true, timeout: 15000, maximumAge: 10000 }
        );
      }
    } catch (error) {
      console.log("Error getting user location:", error);
    }
  };
  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
      base64: true,
    });
    if (!result.canceled) {
      setImageUri(result.assets[0]);
    }
  };

  const data = [
    { key: "1", value: "Client" },
    { key: "2", value: "Mazdoor" },
  ];
  function validateEmail(email) {
    // Regular expression to match a valid email address
    const regex =
      /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/;

    // Check if the email matches the regular expression
    if (regex.test(email)) {
      return true;
    } else {
      return false;
    }
  }
  const handleSubmit = () => {
    if (name === null) {
      Toast.show({
        type: "error",
        position: "top",
        text1: "Please Enter Name",
        visibilityTime: 3000,
      });
      return;
    }
    if (email === null) {
      Toast.show({
        type: "error",
        position: "top",
        text1: "Please Enter Email",
        visibilityTime: 3000,
      });
      return;
    }
    if (password === null) {
      Toast.show({
        type: "error",
        position: "top",
        text1: "Please Enter Valid Password",
        visibilityTime: 3000,
      });
      return;
    }
    if (password.length < 8) {
      Toast.show({
        type: "error",
        position: "top",
        text1: "Please Should be more than 8",
        visibilityTime: 3000,
      });
      return;
    }
    if (selected === null) {
      Toast.show({
        type: "error",
        position: "top",
        text1: "Please Enter User Type",
        visibilityTime: 3000,
      });
      return;
    }
    if (imageUri === null) {
      Toast.show({
        type: "error",
        position: "top",
        text1: "Please Select Image",
        visibilityTime: 3000,
      });
      return;
    }
    if (location === null) {
      Toast.show({
        type: "error",
        position: "top",
        text1: "Please Enter Location",
        visibilityTime: 3000,
      });
      return;
    }

    if (!validateEmail(email)) {
      Toast.show({
        type: "error",
        position: "top",
        text1: "Please Enter Valid Email",
        visibilityTime: 3000,
      });
      return;
    }

    sendData();
    setLoading(true);
  };
  const sendData = async () => {
    try {
      const res = await axios.post(API_URL, {
        name,
        email,
        password,
        accountType: selected,
        image: imageUri.base64,
        location,
      });
      if (res.data) {
        setLoading(false);
        Toast.show({
          type: "success",
          position: "top",
          text1: "Successfully signup!",
          visibilityTime: 3000,
        });
        navigation.navigate("Login");
      }
    } catch (error) {
      console.log(error.message + " error occured");
    }
  };

  const handleButtonPress = () => {
    if (markerLocation) {
      setMapModal(!mapModal);
      setLocation(markerLocation);
    }
  };
  const handleMarkerDragEnd = async (e) => {
    const { latitude, longitude } = e.nativeEvent.coordinate;
    const city = await Location.reverseGeocodeAsync({
      latitude,
      longitude,
    });
    setMarkerLocation({ latitude, longitude, city: city[0].city });
  };
  return (
    <View className="bg-white">
      <ScrollView>
        <View
          className="flex-1 mb-10"
          style={{ backgroundColor: themeColors.bg }}
        >
          <SafeAreaView className="flex pt-8">
            <View className="flex-row justify-start">
              <TouchableOpacity
                onPress={() => navigation.goBack()}
                className="bg-yellow-400 p-2 rounded-tr-2xl rounded-bl-2xl ml-4"
              >
                <AntDesign name="back" size={24} color="black" />
              </TouchableOpacity>
            </View>
            <View className="flex-row justify-center">
              <Image
                source={require("../assets/images/signup.png")}
                style={{ width: 165, height: 110 }}
              />
            </View>
          </SafeAreaView>

          <View
            className="flex-1 bg-white px-8 pt-8"
            style={{ borderTopLeftRadius: 50, borderTopRightRadius: 50 }}
          >
            <View className="form space-y-2">
              <Text className="text-gray-700 ml-4">Full Name</Text>
              <TextInput
                className="p-4 bg-gray-100 text-gray-700 rounded-2xl mb-3"
                onChangeText={(value) => setName(value)}
                placeholder="Enter Name"
              />
              <Text className="text-gray-700 ml-4">Email Address</Text>
              <TextInput
                className="p-4 bg-gray-100 text-gray-700 rounded-2xl mb-3"
                placeholder="Enter Email"
                onChangeText={(value) => setEmail(value)}
                keyboardType="email-address"
              />

              <Text className="text-gray-700 ml-4">Password</Text>
              <TextInput
                className="p-4 bg-gray-100 text-gray-700 rounded-2xl mb-7"
                secureTextEntry
                onChangeText={(value) => setPassword(value)}
                placeholder="Enter Password"
              />
              <SelectList
                setSelected={(val) => setSelected(val)}
                data={data}
                placeholder="Select Account Type"
                save="value"
                search={false}
                className="p-4 bg-gray-100 text-gray-700 rounded-2xl"
                boxStyles={{
                  borderWidth: 0,
                  padding: 4,
                  backgroundColor: "rgb(243,244,246)",
                  marginBottom: 4,
                }}
                dropdownStyles={{
                  borderWidth: 0,
                  backgroundColor: "rgb(243,244,246)",
                }}
              />

              <Text className="text-gray-700 ml-4">Profile Image</Text>
              <TouchableOpacity
                className="text-gray-700 ml-4"
                onPress={pickImage}
              >
                <Entypo name="folder-images" size={24} color="black" />
              </TouchableOpacity>
              <Text className="text-gray-700 ml-4">Select your Location</Text>
              <TouchableOpacity
                className="text-gray-700 ml-4"
                onPress={() => {
                  setMapModal(!mapModal);
                  getUserLocation();
                }}
              >
                <Entypo name="location" size={24} color="black" />
              </TouchableOpacity>
              <TouchableOpacity
                className="py-3 bg-yellow-400 rounded-xl"
                onPress={handleSubmit}
              >
                <Text className="font-xl font-bold text-center text-gray-700">
                  Sign Up
                </Text>
              </TouchableOpacity>
            </View>
            <View className="flex-row justify-center mt-7">
              <Text className="text-gray-500 font-semibold">
                Already have an account?
              </Text>
              <TouchableOpacity onPress={() => navigation.navigate("Login")}>
                <Text className="font-semibold text-yellow-500"> Login</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
        {loading && (
          <View className="flex justify-center items-center w-full h-full z-40 absolute">
            <ActivityIndicator size="large" color="#0000ff" />
          </View>
        )}
        <Modal
          animationType="slide"
          transparent={true}
          visible={mapModal}
          onRequestClose={() => {
            setMapModal(!mapModal);
          }}
        >
          <View className="w-full h-5/6 bottom-0 absolute bg-white rounded-2xl pt-5">
            <View style={{ flex: 1 }}>
              <TouchableOpacity
                onPress={() => setMapModal(!mapModal)}
                className="p-3 rounded-full"
              >
                <AntDesign name="close" size={24} color="black" />
              </TouchableOpacity>
              <MapView
                style={{ flex: 1 }}
                initialRegion={{
                  latitude: currentLocation ? currentLocation.latitude : 0,
                  longitude: currentLocation ? currentLocation.longitude : 0,
                  latitudeDelta: 0.0922,
                  longitudeDelta: 0.0421,
                }}
              >
                {currentLocation && (
                  <Marker
                    draggable
                    coordinate={{
                      latitude: currentLocation.latitude,
                      longitude: currentLocation.longitude,
                    }}
                    title="Current Location"
                    ref={markerRef}
                    onDragEnd={handleMarkerDragEnd}
                  />
                )}
              </MapView>
              <Button title="Select Location" onPress={handleButtonPress} />
            </View>
          </View>
        </Modal>
      </ScrollView>
    </View>
  );
}
