import {
  View,
  Text,
  Image,
  TouchableOpacity,
  Modal,
  Pressable,
  Alert,
  ScrollView,
  ActivityIndicator,
} from "react-native";
import React, { useEffect, useState } from "react";
import { LinearGradient } from "expo-linear-gradient";
import { storeColors } from "../theme";
import StarRating from "react-native-star-rating";
import { AntDesign } from "@expo/vector-icons";
import { useNavigation } from "@react-navigation/native";
import * as Animatable from "react-native-animatable";
import MapView, { Marker } from "react-native-maps";
import { SafeAreaView } from "react-native";
import GradientButton from "../components/gradientButton";
import { useRoute } from "@react-navigation/native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";
import { useIsFocused } from "@react-navigation/native";

export default function Profile() {
  const [modalVisible, setModalVisible] = useState(false);
  const [payment, setPayment] = useState(null);
  const route = useRoute();
  const { id } = route.params;
  const [data, setData] = useState();
  const isFocused = useIsFocused();
  axios.defaults.withCredentials = true;
  const navigation = useNavigation();
  const [uId, setUId] = useState("");
  const [uType, setUType] = useState(null);
  useEffect(() => {
    if (isFocused) {
      getData(id);
      AsyncStorage.getItem("accountType")
        .then((data) => setUType(data))
        .catch((error) => console.log(error));
    }
  }, [isFocused]);
  function getData(id) {
    axios
      .get(`/users`, {
        params: {
          id,
        },
      })
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }
  const handleOrder = () => {
    axios
      .post(`/order`, {
        labor,
        buyerId: id,
        price,
        reqId,
        status: "pending",
      })
      .then((response) => {
        console.log(response.data);
        navigation.goBack();
      })
      .catch((error) => {
        console.log(error);
      });
  };

  if (!data) {
    return (
      <View className="flex justify-center items-center w-full h-full z-40 absolute">
        <ActivityIndicator size="large" color="#0000ff" />
      </View>
    );
  }
  const calculateAverageStars = (feedbackData, sender) => {
    let totalStars = 0;
    let count = 0;

    feedbackData.forEach((feedback) => {
      console.log(feedback.receiver._id);
      if (feedback.receiver._id === sender) {
        totalStars += parseInt(feedback.stars);
        count++;
      }
    });

    const averageStars = count === 0 ? 0 : totalStars / count;
    return averageStars;
  };
  let total = 0;
  total = calculateAverageStars(data.Feedback, id);
  async function deactivateUser(ID) {
    try {
      const response = await axios.patch(`/users/block/${id}`);
      console.log("User status updated successfully:", response.data);
    } catch (error) {
      console.error("An error occurred:", error);
    }
  }
  async function activateUser(ID) {
    try {
      const response = await axios.patch(`/users/unblock/${id}`);
      console.log("User status updated successfully:", response.data);
    } catch (error) {
      console.error("An error occurred:", error);
    }
  }

  return (
    <LinearGradient
      colors={["rgba(58, 131, 244,0.4)", "rgba(9, 181, 211, 0.4)"]}
      className="w-full flex-1"
    >
      <ScrollView>
        <View className="flex-1">
          <View className="relative">
            <Image
              source={{ uri: `data:image/png;base64,${data.image}` }}
              className="w-full h-60 rounded-br-3xl rounded-bl-3xl"
            />
            <LinearGradient
              colors={["transparent", "rgba(0, 0, 0, 0.6)"]}
              className="absolute p-4 h-full w-full flex justify-between rounded-3xl"
            >
              <View className="flex-row justify-start mt-5">
                <TouchableOpacity
                  onPress={() => navigation.goBack()}
                  className="p-3 rounded-full"
                  style={{ backgroundColor: "rgba(255,255,255,0.3)" }}
                >
                  <AntDesign name="back" size={24} color="black" />
                </TouchableOpacity>
              </View>
              <View className="space-y-1">
                <Text className="text-xl font-bold text-gray-300">
                  {data.name}
                </Text>

                <StarRating
                  disabled={true}
                  starSize={15}
                  containerStyle={{ width: 90 }}
                  maxStars={5}
                  halfStarColor="gold"
                  rating={Number(total)}
                  emptyStar={require("../assets/images/emptyStar.png")}
                  fullStar={require("../assets/images/fullStar.png")}
                />
              </View>
            </LinearGradient>
          </View>
          <View className="mx-4 mt-6 space-y-3 h-60 rounded-xl overflow-hidden">
            <MapView
              className="flex-1"
              initialRegion={{
                latitude: data.location.latitude,
                longitude: data.location.longitude,
                latitudeDelta: 0.01,
                longitudeDelta: 0.01,
              }} //your region data goes here.
            >
              {/*Make sure the Marker component is a child of MapView. Otherwise it won't render*/}
              <Marker coordinate={data.location} />
            </MapView>
          </View>

          <View className="mx-4 mt-6  ">
            <Text className="text-3xl font-semibold text-gray-800 mb-2">
              Portfolio
            </Text>
            {data.Portfolio.map((item, key) => {
              return (
                <View
                  className="rounded-xl p-2 mb-1"
                  key={key}
                  style={{ backgroundColor: "rgba(255, 255, 255, 0.4)" }}
                >
                  <Text className="text-gray-600 mb-1 ">
                    Designation : {item.desi}
                  </Text>
                  <Text className="text-gray-600  ">
                    Experience : {item.exp}
                  </Text>
                </View>
              );
            })}
          </View>

          <View className="mt-3">
            <Text className="text-3xl font-semibold text-gray-800 mx-4 mt-2">
              Reviews
            </Text>
            <ScrollView
              showsVerticalScrollIndicator={false}
              contentContainerStyle={{ paddingBottom: 20 }}
            >
              {data.Feedback.map((item, index) => {
                let bg = "rgba(255,255,255,0.4)";

                if (data._id === item.receiver._id) {
                  return (
                    <TouchableOpacity
                      style={{ backgroundColor: bg }}
                      className="mx-4 p-2 mb-2 flex-row rounded-3xl"
                      onPress={() => navigation.navigate("Single")}
                      key={index}
                    >
                      <Image
                        source={{
                          uri: `data:image/png;base64,${item.sender.image}`,
                        }}
                        style={{ width: 80, height: 80 }}
                        className="rounded-2xl"
                      />
                      <View className="flex-1 flex pl-3 ">
                        <Text
                          style={{ color: storeColors.text }}
                          className="font-semibold"
                        >
                          {item.sender.name}
                        </Text>
                        <Text style={{ color: storeColors.text }}>
                          {item.feedback}
                        </Text>
                        <View className="flex-row space-x-3">
                          <View className="flex-row space-x-1">
                            <StarRating
                              disabled={true}
                              starSize={15}
                              containerStyle={{ width: 90 }}
                              maxStars={5}
                              rating={Number(item.stars)}
                              emptyStar={require("../assets/images/emptyStar.png")}
                              fullStar={require("../assets/images/fullStar.png")}
                            />
                          </View>
                        </View>
                      </View>
                    </TouchableOpacity>
                  );
                }
              })}
            </ScrollView>
          </View>
          <View className="mt-6 flex-row justify-center items-center">
            <View>
              {uType == "Admin" && data.status && (
                <LinearGradient
                  colors={["rgba(9, 181, 211, 0.9)", "rgba(58, 131, 244,0.9)"]}
                  end={{ x: 1, y: 1 }}
                  start={{ x: 0.1, y: 0.2 }}
                  className={`rounded-full`}
                >
                  <TouchableOpacity
                    className={`p-3 px-4 `}
                    onPress={() => {
                      deactivateUser(data._id);
                    }}
                  >
                    <Text className="text-white font-bold">Block Account</Text>
                  </TouchableOpacity>
                </LinearGradient>
              )}
              {!data.status && uType == "Admin" && (
                <LinearGradient
                  colors={["rgba(9, 181, 211, 0.9)", "rgba(58, 131, 244,0.9)"]}
                  end={{ x: 1, y: 1 }}
                  start={{ x: 0.1, y: 0.2 }}
                  className={`rounded-full`}
                >
                  <TouchableOpacity
                    className={`p-3 px-4 `}
                    onPress={() => {
                      activateUser(data._id);
                    }}
                  >
                    <Text className="text-white font-bold">
                      Unblock Account
                    </Text>
                  </TouchableOpacity>
                </LinearGradient>
              )}
            </View>
          </View>
        </View>
      </ScrollView>
    </LinearGradient>
  );
}
