import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  Image,
  Alert,
} from "react-native";
import React, { useEffect, useState } from "react";
import { SafeAreaView } from "react-native-safe-area-context";
import { LinearGradient } from "expo-linear-gradient";
import { storeColors } from "../theme";
import { useNavigation } from "@react-navigation/native";
import { Modal } from "react-native";
import { AntDesign } from "@expo/vector-icons";
import AsyncStorage from "@react-native-async-storage/async-storage";
import Form from "./Form";
import { useIsFocused } from "@react-navigation/native";
import axios from "axios";

export default function BuyerAccount() {
  const navigation = useNavigation();
  const [profile, setProfile] = useState(false);
  const isFocused = useIsFocused();
  const [id, setId] = useState("");
  axios.defaults.withCredentials = true;
  const [imageUri, setImageUri] = useState("");
  const [data, setData] = useState("");
  const [accountType, setAccountType] = useState("");
  const [city, setCity] = useState(null);
  const logout = async () => {
    try {
      await AsyncStorage.clear();
      navigation.navigate("Login");
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    if (isFocused) {
      getValueFromAsyncStorage();
    }
  }, [isFocused]);
  async function getValueFromAsyncStorage() {
    try {
      const value = await AsyncStorage.getItem("id");
      const typr = await AsyncStorage.getItem("accountType");
      if (typr !== null) {
        setAccountType(typr);
      }
      const cityF = await AsyncStorage.getItem("city");
      if (cityF !== null) {
        setCity(cityF);
      }
      if (value !== null) {
        const userData = await axios.get("/users", {
          params: {
            id: value,
          },
        });
        if (userData) {
          setData(userData.data);
        }
        setId(value);
      } else {
        await AsyncStorage.clear();
        navigation.navigate("Login");
      }
    } catch (error) {
      console.log(error);
    }
  }
  return (
    <LinearGradient
      colors={["rgba(58, 131, 244,0.4)", "rgba(9, 181, 211, 0.4)"]}
      className="w-full flex-1"
    >
      <View className="relative">
        <Image
          source={{ uri: `data:image/png;base64,${data.image}` }}
          className="w-full h-60 rounded-3xl"
        />
        <LinearGradient
          colors={["transparent", "rgba(0, 0, 0, 0.6)"]}
          className="absolute p-4 h-full w-full flex justify-end rounded-3xl"
        >
          <View className="space-y-1 ">
            <Text className="text-xl font-bold text-gray-300">{data.name}</Text>
          </View>
        </LinearGradient>
      </View>
      <SafeAreaView>
        <View className="container">
          <View className="mt-3">
            <ScrollView showsVerticalScrollIndicator={false}>
              {accountType != "Admin" && (
                <TouchableOpacity
                  style={{ backgroundColor: "rgba(255,255,255,0.4)" }}
                  className="mx-4 p-2 mb-2 flex-row rounded-3xl"
                  onPress={() => {
                    setProfile(!profile);
                  }}
                >
                  <View className="flex-1 flex justify-center pl-3 space-y-3">
                    <Text
                      style={{ color: storeColors.text }}
                      className="font-semibold text-lg"
                    >
                      Post a request
                    </Text>
                  </View>
                </TouchableOpacity>
              )}

              <TouchableOpacity
                style={{ backgroundColor: "rgba(255,255,255,0.4)" }}
                className="mx-4 p-2 mb-2 flex-row rounded-3xl"
                onPress={logout}
              >
                <View className="flex-1 flex justify-center pl-3 space-y-3">
                  <Text
                    style={{ color: storeColors.text }}
                    className="font-semibold text-lg"
                  >
                    Logout
                  </Text>
                </View>
              </TouchableOpacity>
            </ScrollView>
          </View>
        </View>
        <Modal
          animationType="slide"
          transparent={true}
          visible={profile}
          onRequestClose={() => {
            setProfile(!profile);
          }}
        >
          <View className="w-full h-5/6 bottom-0 absolute bg-white rounded-2xl p-5">
            <View>
              <View className="flex-row justify-end">
                <TouchableOpacity
                  onPress={() => setProfile(!profile)}
                  className="p-3 rounded-full"
                  style={{ backgroundColor: "rgba(255,255,255,0.3)" }}
                >
                  <AntDesign name="close" size={24} color="black" />
                </TouchableOpacity>
              </View>
              {console.log(city)}
              <Form id={id} setProfile={setProfile} city={city} />
            </View>
          </View>
        </Modal>
      </SafeAreaView>
    </LinearGradient>
  );
}
