import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  Modal,
  Alert,
  ActivityIndicator,
  TextInput,
} from "react-native";
import React, { useEffect, useState } from "react";
import { SafeAreaView } from "react-native-safe-area-context";
import { LinearGradient } from "expo-linear-gradient";
import { storeColors } from "../theme";
import Toast from "react-native-toast-message";
import { Feather, FontAwesome, AntDesign } from "@expo/vector-icons";
import Request from "../components/Request";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";
import { useIsFocused } from "@react-navigation/native";
import * as Location from "expo-location";

export default function SellerDashBoard() {
  const [profile, setProfile] = useState(false);
  const [data, setData] = useState("");
  const [id, setId] = useState("");
  const isFocused = useIsFocused();
  const [desc, setDesc] = useState("");
  const [price, setPrice] = useState("");
  const [buyerId, setBuyerId] = useState("");
  const [reqId, setReqId] = useState("");
  const [loading, setLoading] = useState(false);
  axios.defaults.withCredentials = true;

  useEffect(() => {
    requestLocationPermission();
  }, []);
  const requestLocationPermission = async () => {
    try {
      const { status } = await Location.requestForegroundPermissionsAsync();
      if (status !== "granted") {
        console.log("Location permission not granted!");
        return;
      }
      getUserLocation();
    } catch (error) {
      console.log("Error requesting location permission:", error);
    }
  };
  const [city, setCity] = useState(null);
  const getUserLocation = async () => {
    try {
      if (Platform.OS === "android") {
        const location = await Location.getCurrentPositionAsync({});
        const city = await Location.reverseGeocodeAsync({
          latitude: location.coords.latitude,
          longitude: location.coords.longitude,
        });
        setCity(city[0].city);
      }
    } catch (error) {
      console.log("Error getting user location:", error);
    }
  };
  useEffect(() => {
    if (isFocused) {
      getValueFromAsyncStorage();
    }
  }, [isFocused, city]);
  async function getValueFromAsyncStorage() {
    try {
      const value = await AsyncStorage.getItem("id");
      if (value !== null) {
        setId(value);
        getData(value, city);
      } else {
        await AsyncStorage.clear();
        navigation.navigate("Login");
      }
    } catch (error) {
      console.log(error);
    }
  }
  if (!data) {
    return (
      <View className="flex justify-center items-center w-full h-full z-40 absolute">
        <ActivityIndicator size="large" color="#0000ff" />
      </View>
    );
  }
  function getData(value, city) {
    axios
      .get(`/request/`, {
        params: {
          id: value,
          city,
        },
      })
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }
  const calculateAverageStars = (feedbackData, sender) => {
    let totalStars = 0;
    let count = 0;

    feedbackData.forEach((feedback) => {
      if (feedback.receiver === sender) {
        totalStars += parseInt(feedback.stars);
        count++;
      }
    });

    const averageStars = count === 0 ? 0 : totalStars / count;
    return averageStars;
  };
  const handleSubmit = () => {
    if (desc.trim() === "" || price.trim() === "") {
      Toast.show({
        type: "error",
        position: "top",
        text1: "please fill details",
        visibilityTime: 3000,
      });
    } else {
      setLoading(true);
      sendData();
    }
  };
  const sendData = async () => {
    try {
      const res = await axios.post("/proposal", {
        description: desc,
        price,
        buyerId,
        labor: id,
        reqId,
      });
      if (res.data) {
        Toast.show({
          type: "success",
          position: "top",
          text1: "Successfully submitted!",
          visibilityTime: 3000,
        });
        setLoading(false);
        setProfile(false);
        setData("");
        getData(id);
      }
    } catch (error) {
      console.log(error.message + " error occured");
      setLoading(false);
      Toast.show({
        type: "error",
        position: "top",
        text1: "something went wrong",
        visibilityTime: 3000,
      });
    }
  };

  return (
    <LinearGradient
      colors={["rgba(58, 131, 244,0.4)", "rgba(9, 181, 211, 0.4)"]}
      className="w-full flex-1"
    >
      <SafeAreaView>
        <View className="container">
          {/* categories */}
          <View className="mt-3 space-y-4">
            <Text
              style={{ color: storeColors.text }}
              className="ml-4 text-3xl font-bold"
            >
              Browse Requests
            </Text>
          </View>

          {/* top action games list */}
          <View className="mt-3">
            <ScrollView
              showsVerticalScrollIndicator={false}
              contentContainerStyle={{ paddingBottom: 150 }}
            >
              {data.map((item, index) => {
                let total = 0;
                total = calculateAverageStars(
                  item.buyerId.Feedback,
                  item.buyerId._id
                );
                return (
                  <TouchableOpacity
                    key={index}
                    onPress={() => {
                      setBuyerId(item.buyerId._id);
                      setReqId(item._id);
                      setProfile(!profile);
                    }}
                  >
                    <Request
                      title={item.title}
                      desc={item.description}
                      type={item.rateType}
                      budget={item.price}
                      image={item.buyerId.image}
                      name={item.buyerId.name}
                      total={total}
                    />
                  </TouchableOpacity>
                );
              })}
            </ScrollView>
          </View>
        </View>
        <Modal
          animationType="slide"
          transparent={true}
          visible={profile}
          onRequestClose={() => {
            Alert.alert("Modal has been closed.");
            setProfile(!profile);
          }}
        >
          <View className="w-full h-5/6 bottom-0 absolute bg-white rounded-2xl p-5">
            <View>
              <View className="flex-row justify-end">
                <TouchableOpacity
                  onPress={() => setProfile(!profile)}
                  className="p-3 rounded-full"
                  style={{ backgroundColor: "rgba(255,255,255,0.3)" }}
                >
                  <AntDesign name="close" size={24} color="black" />
                </TouchableOpacity>
              </View>
              <ScrollView showsVerticalScrollIndicator={false}>
                <View className="form space-y-2">
                  <Text className="text-gray-700 ml-2">Description</Text>
                  <TextInput
                    className="h-56 p-4 bg-gray-100 text-gray-700 rounded-2xl mb-3 "
                    textAlignVertical="top"
                    textAlign="left"
                    onChangeText={(value) => setDesc(value)}
                  />
                  <Text className="text-gray-700 ml-2">Price</Text>
                  <TextInput
                    className=" p-4 bg-gray-100 text-gray-700 rounded-2xl mb-3"
                    onChangeText={(value) => setPrice(value)}
                  />
                  <TouchableOpacity
                    className="py-3  bg-yellow-400 rounded-xl"
                    onPress={handleSubmit}
                  >
                    <Text className="text-xl font-bold text-center text-gray-700">
                      Submit
                    </Text>
                  </TouchableOpacity>
                </View>
              </ScrollView>
            </View>
          </View>
          {loading && (
            <View className="flex justify-center items-center w-full h-full z-40 absolute">
              <ActivityIndicator size="large" color="#0000ff" />
            </View>
          )}
        </Modal>
      </SafeAreaView>
    </LinearGradient>
  );
}
