import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  Image,
  ActivityIndicator,
  Modal,
} from "react-native";
import React, { useEffect, useState } from "react";
import { SafeAreaView } from "react-native-safe-area-context";
import { LinearGradient } from "expo-linear-gradient";
import { storeColors } from "../theme";
import GradientButton from "../components/gradientButton";
import { Feather, FontAwesome, AntDesign } from "@expo/vector-icons";
import { useIsFocused, useNavigation } from "@react-navigation/native";
import axios from "axios";
import { TextInput } from "react-native-gesture-handler";

export default function ComplaintScreen() {
  const [reports, setReports] = useState("");
  const isFocused = useIsFocused();
  const navigation = useNavigation();
  const [profile, setProfile] = useState("");
  const [response, setResponse] = useState("");
  const [reportId, setReportId] = useState("");
  useEffect(() => {
    if (isFocused) {
      getData();
    }
  }, [isFocused]);
  const getData = () => {
    axios
      .get(`/report`)
      .then((response) => {
        setReports(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  if (!reports) {
    return (
      <View className="flex justify-center items-center w-full h-full z-40 absolute">
        <ActivityIndicator size="large" color="#0000ff" />
      </View>
    );
  }
  const handleSubmit = () => {
    // console.log(reportId, response);
    axios
      .patch(`/report/${reportId}`, { response: response })
      .then((response) => {
        console.log("Report updated successfully:", response.data);
        setProfile(!profile);
        getData();
      })
      .catch((error) => {
        console.log("Error updating report:", error);
      });
  };
  return (
    <LinearGradient
      colors={["rgba(58, 131, 244,0.4)", "rgba(9, 181, 211, 0.4)"]}
      className="w-full flex-1"
    >
      <SafeAreaView>
        <ScrollView showsVerticalScrollIndicator={false}>
          <View className="container">
            {/* categories */}
            <View className="mt-3 space-y-4">
              <Text
                style={{ color: storeColors.text }}
                className="ml-4 text-3xl font-bold"
              >
                Complaints
              </Text>
            </View>

            <View className="mt-3">
              {reports.map((item, index) => {
                let bg = "rgba(255,255,255,0.4)";

                return (
                  <TouchableOpacity
                    style={{ backgroundColor: bg }}
                    className="mx-4 p-2 mb-2 flex-row rounded-3xl"
                    onPress={() => {
                      setProfile(!profile);
                      setReportId(item._id);
                    }}
                    key={index}
                  >
                    <Image
                      source={{
                        uri: `data:image/png;base64,${item.buyerId.image}`,
                      }}
                      style={{ width: 80, height: 80 }}
                      className="rounded-2xl"
                    />
                    <View className="flex-1 flex  pl-3 space-y-3">
                      <Text
                        style={{ color: storeColors.text }}
                        className="font-semibold"
                      >
                        {item.buyerId.name}
                      </Text>
                      <Text
                        style={{ color: storeColors.text }}
                        className="text-gray-500"
                      >
                        {item.message}
                      </Text>
                    </View>
                    <TouchableOpacity
                      onPress={() =>
                        navigation.navigate("Profile", { id: item.labor._id })
                      }
                      className="flex justify-center items-center"
                    >
                      <GradientButton
                        value="View Labour"
                        buttonClass="py-2 px-5"
                      />
                    </TouchableOpacity>
                  </TouchableOpacity>
                );
              })}
            </View>
          </View>
          <Modal
            animationType="slide"
            transparent={true}
            visible={profile}
            onRequestClose={() => {
              Alert.alert("Modal has been closed.");
              setProfile(!profile);
            }}
          >
            <View className="w-full h-5/6 bottom-0 absolute bg-white rounded-2xl p-5">
              <View>
                <View className="flex-row justify-end">
                  <TouchableOpacity
                    onPress={() => setProfile(!profile)}
                    className="p-3 rounded-full"
                    style={{ backgroundColor: "rgba(255,255,255,0.3)" }}
                  >
                    <AntDesign name="close" size={24} color="black" />
                  </TouchableOpacity>
                </View>
                <ScrollView showsVerticalScrollIndicator={false}>
                  <View className="form space-y-2">
                    <Text className="text-gray-700 ml-2">Reply</Text>
                    <TextInput
                      className="h-56 p-4 bg-gray-100 text-gray-700 rounded-2xl mb-3 "
                      textAlignVertical="top"
                      textAlign="left"
                      onChangeText={(value) => setResponse(value)}
                      placeholder="your reply"
                    />

                    <TouchableOpacity
                      className="py-3  bg-yellow-400 rounded-xl"
                      onPress={handleSubmit}
                    >
                      <Text className="text-xl font-bold text-center text-gray-700">
                        Submit
                      </Text>
                    </TouchableOpacity>
                  </View>
                </ScrollView>
              </View>
            </View>
          </Modal>
        </ScrollView>
      </SafeAreaView>
    </LinearGradient>
  );
}
