import { View, Text, ScrollView, TouchableOpacity, Image } from "react-native";
import React, { useEffect, useState } from "react";
import { SafeAreaView } from "react-native-safe-area-context";
import { LinearGradient } from "expo-linear-gradient";
import { storeColors } from "../theme";
import { useNavigation } from "@react-navigation/native";
import { Modal } from "react-native";
import { AntDesign } from "@expo/vector-icons";
import PortfolioForm from "../components/PortfolioForm";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useIsFocused } from "@react-navigation/native";
import axios from "axios";
import { TextInput } from "react-native-gesture-handler";
import Toast from "react-native-toast-message";

export default function SellerAccount() {
  const [profile, setProfile] = useState(false);
  const navigation = useNavigation();
  const [imageUri, setImageUri] = useState("");
  const [data, setData] = useState("");
  const isFocused = useIsFocused();
  const [id, setId] = useState("");
  const [designation, setDesingation] = useState("");
  const [expreience, setExperience] = useState("");
  axios.defaults.withCredentials = true;
  const formData = [
    { key: "1", value: "Flat Rate" },
    { key: "2", value: "Hour Rate" },
  ];
  const logout = async () => {
    try {
      await AsyncStorage.clear();
      navigation.navigate("Login");
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    if (isFocused) {
      getValueFromAsyncStorage();
    }
  }, [isFocused]);
  async function getValueFromAsyncStorage() {
    try {
      const value = await AsyncStorage.getItem("id");
      if (value !== null) {
        const userData = await axios.get("/users", {
          params: {
            id: value,
          },
        });
        if (userData) {
          setData(userData.data);
        }
        setId(value);
      } else {
        await AsyncStorage.clear();
        navigation.navigate("Login");
      }
    } catch (error) {
      console.log(error);
    }
  }
  const handlePortfolio = async () => {
    setProfile(!profile);
    const update = await axios.patch(`/users/${id}`, {
      desi: designation,
      exp: expreience,
    });
    const result = update.data;
    if (result == "updated") {
      Toast.show({
        type: "success",
        position: "top",
        text1: "Detail Added",
        visibilityTime: 3000,
      });
    }
  };
  return (
    <LinearGradient
      colors={["rgba(58, 131, 244,0.4)", "rgba(9, 181, 211, 0.4)"]}
      className="w-full flex-1"
    >
      <View className="relative">
        <Image
          source={{ uri: `data:image/png;base64,${data.image}` }}
          className="w-full h-60 rounded-3xl"
        />
        <LinearGradient
          colors={["transparent", "rgba(0, 0, 0, 0.6)"]}
          className="absolute p-4 h-full w-full flex justify-end rounded-3xl"
        >
          <View className="space-y-1 ">
            <Text className="text-xl font-bold text-gray-300">{data.name}</Text>
          </View>
        </LinearGradient>
      </View>
      <SafeAreaView>
        <View className="container">
          <View className="mt-3">
            <ScrollView showsVerticalScrollIndicator={false}>
              <TouchableOpacity
                style={{ backgroundColor: "rgba(255,255,255,0.4)" }}
                className="mx-4 p-2 mb-2 flex-row rounded-3xl"
                onPress={() => navigation.navigate("Profile", { id })}
              >
                <View className="flex-1 flex justify-center pl-3 space-y-3">
                  <Text
                    style={{ color: storeColors.text }}
                    className="font-semibold text-lg"
                  >
                    My Profile
                  </Text>
                </View>
              </TouchableOpacity>
              <TouchableOpacity
                style={{ backgroundColor: "rgba(255,255,255,0.4)" }}
                className="mx-4 p-2 mb-2 flex-row rounded-3xl"
                onPress={() => {
                  setProfile(!profile);
                }}
              >
                <View className="flex-1 flex justify-center pl-3 space-y-3">
                  <Text
                    style={{ color: storeColors.text }}
                    className="font-semibold text-lg"
                  >
                    Add Portfolio
                  </Text>
                </View>
              </TouchableOpacity>

              <TouchableOpacity
                style={{ backgroundColor: "rgba(255,255,255,0.4)" }}
                className="mx-4 p-2 mb-2 flex-row rounded-3xl"
                onPress={logout}
              >
                <View className="flex-1 flex justify-center pl-3 space-y-3">
                  <Text
                    style={{ color: storeColors.text }}
                    className="font-semibold text-lg"
                  >
                    Logout
                  </Text>
                </View>
              </TouchableOpacity>
            </ScrollView>
          </View>
        </View>
        <Modal
          animationType="slide"
          transparent={true}
          visible={profile}
          onRequestClose={() => {
            Alert.alert("Modal has been closed.");
            setProfile(!profile);
          }}
        >
          <View className="w-full h-5/6 bottom-0 absolute bg-white rounded-2xl p-5">
            <View>
              <View className="flex-row justify-end">
                <TouchableOpacity
                  onPress={() => setProfile(!profile)}
                  className="p-3 rounded-full"
                  style={{ backgroundColor: "rgba(255,255,255,0.3)" }}
                >
                  <AntDesign name="close" size={24} color="black" />
                </TouchableOpacity>
              </View>
              <ScrollView showsVerticalScrollIndicator={false}>
                <View className="form space-y-2">
                  <Text className="text-gray-700 ">Designation</Text>
                  <TextInput
                    onChangeText={(value) => setDesingation(value)}
                    className="p-4 bg-gray-100 text-gray-700 rounded-2xl mb-3"
                    placeholder="sweeper"
                  />
                  <Text className="text-gray-700 ">Experience</Text>
                  <TextInput
                    className="p-4 bg-gray-100 text-gray-700 rounded-2xl mb-3"
                    onChangeText={(value) => setExperience(value)}
                    placeholder="6 Months"
                  />

                  <TouchableOpacity
                    className="py-3  bg-yellow-400 rounded-xl"
                    onPress={handlePortfolio}
                  >
                    <Text className="text-xl font-bold text-center text-gray-700">
                      Submit
                    </Text>
                  </TouchableOpacity>
                </View>
              </ScrollView>
            </View>
          </View>
        </Modal>
      </SafeAreaView>
    </LinearGradient>
  );
}
